import React, { useState, useEffect } from "react";
import { Checkbox } from "@thumbtack/thumbprint-react";

import FormInput from "../../components/form-input/form-input.component";
import Search from "../search/search";
import CustomButton from "../../components/custom-button/custom-button.component";
import Dropdown from "../../components/dropdown/Dropdown";
import SearchPatient from "../../hooks/searchPatient";
//import Checkbox from "../../components/checkbox/checkbox";
import { getPatient } from "../../apis";

import { StatusDiv } from "./status.styles";

const options = [
  {
    label: "Severe",
    value: "severe",
  },
  {
    label: "Moderate",
    value: "moderate",
  },
  {
    label: "Mild",
    value: "mild",
  },
];

const PatientStatus = () => {
  //const [patientId, setPatientId] = useState("1055");
  const [selected, setSelected] = useState(options[0]);
  //const [error, setError] = useState(false);
  //const [patient, setPatient] = useState("");
  const [patient, search] = SearchPatient("1055");
  const [result, setResult] = useState({
    patient_id: "",
    patientId: "",
    patientName: "",
    bedNo: "",
    ventilator: "",
    currentStatus: "",
  });
  //console.log(patient);
  const {
    patient_id,
    patientId,
    patientName,
    bedNo,
    ventilator,
    currentStatus,
  } = result;

  useEffect(() => {
    if (patient) {
      setResult({
        ...result,
        patient_id: patient._id,
        patientId: patient.patientId,
        patientName: patient.patientName,
        currentStatus: patient.currentStatus,
        ventilator: patient.ventilator,
        bedNo: patient.bedNo,
      });
    }
  }, [patient]);

  const [isChecked, setIsChecked] = React.useState(undefined);

  // const handleSearch = (name) => (event) => {
  //   setPatientId(event.target.value);
  // };

  // useEffect(() => {
  //   setPatient(patient);
  //   console.log(patient);
  // }, patient);

  return (
    <StatusDiv>
      <Search onFormSubmit={search} />
      <form className="status-form">
        <FormInput
          type="text"
          name="patientName"
          value={patientName}
          onChange=""
          label="Patient Name"
          required
        />
        <Dropdown
          label="Current Status"
          options={options}
          selected={currentStatus}
          onSelectedChange={setSelected}
        />
        <FormInput
          type="text"
          name="bedNo"
          value={bedNo}
          onChange=""
          label="Bed #"
          required
        />
        {/*<Checkbox
          type="text"
          label="Ventilator"
          isSelected=""
          onCheckboxChange=""
          required
        />*/}
        <Checkbox
          name="ventilator"
          isChecked={ventilator}
          onChange={setIsChecked}
        >
          Ventilator
        </Checkbox>
        <CustomButton type="submit">Submit</CustomButton>
      </form>
    </StatusDiv>
  );
};

export default PatientStatus;
